import React, {Component} from 'react';
import './App.css'

class App extends Component {

  constructor(props){
    super(props);

    this.state={
      newItem:"",
      list: []
    }
  }



updateInput(key, value){
  // Update the react state
  this.setState({
    [key]:value  })
}

addItem(){
  // Create item with an unique id.

  const newItem = {
    id: 1+ Math.random(),
    value: this.state.newItem.slice()
  };

  // Copy the current list of items
  const list = [...this.state.list];


  // Add new item to the list

  list.push(newItem);

  // Update the state with new list and reset newItem input


  this.setState({
    list,
    newItem:""

  });
}

deleteItem(id){

  const list = [...this.state.list];

  const updatedList = list.filter(item => item.id !== id);

  this.setState({list: updatedList});
}


render() {
  return (
    <div className="App">
      <div>
        Add a todo.
        <br/>
        <input type="text"
        placeholder="Type a todo here..."
        value={this.state.newItem}
        onChange={e => this.updateInput("newItem", e.target.value)}/>

        <button
        onClick={() => this.addItem()}>Add</button>
        <br/>
        <ul>
          {this.state.list.map(item => {
            return(
              <li key={item.id}>
              {item.value} <button onClick={() => this.deleteItem(item.id)}>X</button>
              </li>
            )
          })}
        </ul>
      </div>
    </div>
  );
  }
}


export default App;
