import React, {Component} from 'react';


class App extends Component {

  constructor(props){
    super(props);

    this.state={
      newItem:"",
      list: []
    }
  }


render() {
  return (
    <div className="App">
      <div>
        Add a todo.
        <br/>
        <input type="text"
        placeholder="Type a todo here..."
        value={this.state.newItem}
        onChange={e => this.updateInput("newItem", e.target.value)}/>

        <button
        onClick={() => this.addItem}></button>
      </div>
    </div>
  );
  }

}

export default App;
